    private String leerArchivo(String direccion){
        String texto = "";
        
        try{
            BufferedReader bf = new BufferedReader(new FileReader(direccion));
            String temp = "";
            String bfRead;
            while((bfRead = bf.readLine()) != null){
                temp = temp + bfRead+"\n";
            }
            texto = temp;
        }catch(Exception e){
        
        }
        return texto;
    }

